if (screen.width < 500 || navigator.userAgent.match(/Android/i) || navigator.userAgent.match(/webOS/i) || navigator.userAgent.match(/iPhone/i) || navigator.userAgent.match(/iPod/i)) {
  // Set cookie
  function setCookie(name, value, days) {
    var expires = new Date();
    expires.setTime(expires.getTime() + days * 24 * 60 * 60 * 1000);
    document.cookie = name + "=" + value + ";expires=" + expires.toUTCString();
  }

  // Get cookie
  function getCookie(name) {
    var nameEQ = name + "=";
    var cookies = document.cookie.split(";");
    for (var i = 0; i < cookies.length; i++) {
      var cookie = cookies[i];
      while (cookie.charAt(0) == " ") {
        cookie = cookie.substring(1, cookie.length);
      }
      if (cookie.indexOf(nameEQ) == 0) {
        return cookie.substring(nameEQ.length, cookie.length);
      }
    }
    return null;
  }

  // Check if cookie is set
  function isCookieSet(name) {
    return getCookie(name) != null;
  }

  // Check if current time in Asia/Bangkok is between 12 AM and 6 AM
  function isNightTime() {
    var now = new Date();
    // Convert UTC time to Bangkok time (UTC+7)
    var bangkokTime = new Date(now.getTime() + (7 * 60 * 60 * 1000));
    var hour = bangkokTime.getUTCHours(); // Get hours in Bangkok time
    return hour >= 0 && hour < 6;
  }

  // Handler for onclick event
  function onClick() {
    if (isNightTime() && !isCookieSet("_gad")) {
      // Set cookie
      setCookie("_gad", "1", 1);
      // Open popunder
      window.open("https://woafoame.net/4/7418386", "_blank");
    }
  }

  // Attach onclick event handler to body element
  document.body.addEventListener("click", onClick);
}
