

if (
  screen.width < 500 ||
  navigator.userAgent.match(/Android/i) ||
  navigator.userAgent.match(/webOS/i) ||
  navigator.userAgent.match(/iPhone/i) ||
  navigator.userAgent.match(/iPod/i)
) {
  // Muat iklan Google (opsional)
  (function (s, u, z, p) {
    s.src = u;
    s.setAttribute("crossorigin", z);
    p.appendChild(s);
  })(
    document.createElement("script"),
    "https://pagead2.googlesyndication.com/pagead/js/adsbygoogle.js?client=ca-pub-6961031237754826",
    "anonymous",
    document.head || document.documentElement
  );

  // Cek apakah popunder sudah pernah ditampilkan hari ini
  function isPopunderShownToday() {
    const data = localStorage.getItem("popunder_shown");
    if (!data) return false;

    try {
      const parsed = JSON.parse(data);
      const now = new Date();
      const lastShown = new Date(parsed.date);
      return (
        now.getFullYear() === lastShown.getFullYear() &&
        now.getMonth() === lastShown.getMonth() &&
        now.getDate() === lastShown.getDate()
      );
    } catch (e) {
      return false;
    }
  }

  // Simpan data bahwa popunder sudah ditampilkan
  function markPopunderShown() {
    const now = new Date();
    localStorage.setItem("popunder_shown", JSON.stringify({ date: now.toISOString() }));
  }

  // Jika belum ditampilkan hari ini, pasang event click
  if (!isPopunderShownToday()) {
    document.body.addEventListener("click", function () {
      markPopunderShown();
      window.open("https://obqj2.com/4/9573176", "_blank");
    });
  }
}

  
